import { error } from "console";
import Pokemon from "../models/pokemon";

export default class PokemonService {

  static async getPokemons(): Promise<Pokemon[]> {
    const response = await fetch('http://localhost:3000/pokemons');
    return await response.json();
  }

  static getPokemon(id: number): Promise<Pokemon | null> {
    return fetch(`http://localhost:3000/pokemons/${id}`)
      .then(response => response.json())
      .then(data => this.isEmpty(data) ? null : data)
      .catch(this.handleError);  // Vous pouvez gérer les erreurs ici si nécessaire
  }
  static updatePokemon(pokemon: Pokemon): Promise<Pokemon> {
    return fetch(`http://localhost:3000/pokemons/${pokemon.id}`, {  // Corrigé ici
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'  // Ajout des headers pour indiquer le format des données
      },
      body: JSON.stringify(pokemon)
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .catch(error => this.handleError(error));
}

  static isEmpty(data: object): boolean {
    return Object.keys(data).length === 0;
  }

  static handleError(error: Error): void {
    console.error(error);
  }
}
